<!doctype html> <html lang="en"> <head> <meta charset="utf-8" /> <meta name="viewport" content="width=device-width, initial-scale=1" /> <title>Kyle Cuadero - Booking Site</title> <link rel="preconnect" href="https://fonts.googleapis.com"> <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin> <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700;800&display=swap" rel="stylesheet"> <style> :root{ --bg:#0b0f28; --bg-2:#0a0e26; --card:rgba(255,255,255,0.06); --muted:rgba(255,255,255,0.75); --line:rgba(255,255,255,0.08); --primary:#6c5ce7; --primary-2:#a56cff; --text:#ffffff; --radius:16px; --radius-sm:10px; --shadow:0 8px 24px rgba(0,0,0,.45); } *{box-sizing:border-box} html,body{height:100%} body{margin:0;background:linear-gradient(180deg,#0b0f28 0%, #05081a 100%);font-family:Inter,Arial,Helvetica,sans-serif;color:var(--text);-webkit-font-smoothing:antialiased} a{text-decoration:none;color:var(--text)} .container{width:92%;max-width:1200px;margin:0 auto} .nav{position:sticky;top:0;z-index:50;background:rgba(14,18,45,0.6);backdrop-filter:blur(6px);border-bottom:1px solid var(--line)} .nav-inner{display:flex;align-items:center;justify-content:space-between;padding:14px 0} .logo{display:flex;align-items:center;gap:10px;font-weight:800;font-size:20px;letter-spacing:0.5px} .nav-links a{margin:0 12px;font-weight:600;opacity:.95} .nav-links a.active{color:var(--primary)} .cta{padding:8px 14px;border:1px solid var(--line);border-radius:10px;font-weight:700} .hero{ position:relative; padding:100px 0 40px; background: linear-gradient(180deg, rgba(11,15,40,0.5) 0%, rgba(11,15,40,0.95) 100%), url('assets/bg.jpg'); background-size:cover; background-position:center; } .hero .container{text-align:center} .hero h1{ font-size:42px; line-height:1.02; margin:0 0 10px; font-weight:800; letter-spacing:0.4px; } .hero p{font-size:16px;color:var(--muted);margin:0 auto 22px;max-width:760px} /* search card */ .search-card{max-width:980px;margin:18px auto 0;padding:18px;border-radius:14px;background:linear-gradient(90deg, rgba(255,255,255,0.03), rgba(255,255,255,0.02));border:1px solid var(--line);box-shadow:var(--shadow);display:flex;flex-direction:column;gap:12px} .search-tabs{display:flex;gap:10px;justify-content:center} .tab{padding:10px 18px;border-radius:999px;background:transparent;border:1px solid var(--line);font-weight:700;cursor:pointer;user-select:none} .tab.active{background:var(--primary);border-color:transparent;color:#fff;box-shadow:0 6px 20px rgba(108,92,231,0.16)} .search-form{display:grid;grid-template-columns:2fr 1fr 1fr auto;gap:10px;align-items:center} .input,select{background:#0f1430;color:#fff;border:1px solid var(--line);padding:12px 12px;border-radius:10px;width:100%;outline:none;font-size:14px} .search-btn{background:var(--primary);padding:12px 18px;border:0;border-radius:10px;color:#fff;font-weight:800;cursor:pointer} .icon{opacity:.9;margin-right:8px} /* features */ .section{padding:52px 0} .section h3{margin:0 0 14px;font-size:20px} .cards{display:grid;grid-template-columns:repeat(auto-fit,minmax(220px,1fr));gap:16px} .card{background:var(--card);border:1px solid var(--line);border-radius:12px;overflow:hidden;box-shadow:var(--shadow)} .card img{width:100%;height:220px;object-fit:cover;display:block} .card .pad{padding:12px} .card h4{margin:0;font-size:16px} .card p{margin:8px 0 0;color:var(--muted);font-size:13px} .card .row{display:flex;justify-content:space-between;align-items:center;margin-top:12px;gap:8px} .badge{display:inline-block;padding:6px 10px;border-radius:999px;background:rgba(255,255,255,0.03);border:1px solid var(--line);font-size:12px;color:var(--muted)} /* responsive */ @media (max-width:920px){ .search-form{grid-template-columns:1fr 1fr 1fr auto} .hero h1{font-size:36px} } @media (max-width:700px){ .nav-inner{padding:12px 0} .nav-links a{display:inline-block} .search-card{padding:14px} .search-form{grid-template-columns:1fr;gap:10px} .search-tabs{justify-content:space-between} .hero{padding:70px 0 30px} .hero h1{font-size:30px} } .footer{padding:24px 0;border-top:1px solid var(--line);background:rgba(14,18,45,0.6);backdrop-filter:blur(6px);text-align:center;color:var(--muted)} </style> </head> <body> <?php include __DIR__ . '/shared-nav.php'; ?> <header class="hero" id="top"> <div class="container"> <h1>GET MOVIE TICKETS</h1> <p>Buy movie tickets in advance, find movie times with trailers, read reviews and much more.</p> <!-- Search Card --> <div class="search-card" role="region" aria-label="Search movies and events"> <div class="search-tabs" role="tablist" aria-label="Categories"> <div class="tab active" data-tab="movies" role="tab" aria-selected="true">MOVIES</div> <div class="tab" data-tab="events" role="tab" aria-selected="false">EVENTS</div> <div class="tab" data-tab="sports" role="tab" aria-selected="false">SPORTS</div> </div> <!-- GET form: sends to movies.php (or events.php) via GET so it's simple and bookmarkable --> <form id="searchForm" class="search-form" method="GET" action="movies.php" role="search" aria-label="Search form"> <input class="input" id="q" name="q" type="search" placeholder="Search for Movies" aria-label="Search query"> <select name="city" id="city" class="input" aria-label="City"> <option value="">All cities</option> <option>London</option> <option>New York</option> <option>Los Angeles</option> <option>Philippines</option> </select> <input class="input" id="date" name="date" type="date" aria-label="Date"> <button type="submit" id="searchBtn" class="search-btn">Search</button> <!-- hidden input to carry current category --> <input type="hidden" name="category" id="category" value="movies"> </form> </div> </div> </header> <section class="section"> <div class="container"> <h3>Featured Movies</h3> <div class="cards"> <div class="card"> <img src="assets/m1.jpg" alt="Nightfall City poster"> <div class="pad"> <h4>Nightfall City</h4> <p>Sci-Fi • 2h 10m</p> <div class="row"> <span class="badge">IMAX</span> <a class="badge" href="movies.php?service=<?php echo rawurlencode('Movie - Nightfall City'); ?>">View</a> </div> </div> </div> <div class="card"> <img src="assets/m2.jpg" alt="Red Horizon poster"> <div class="pad"> <h4>Red Horizon</h4> <p>Action • 1h 58m</p> <div class="row"> <span class="badge">Dolby</span> <a class="badge" href="movies.php?service=<?php echo rawurlencode('Movie - Red Horizon'); ?>">View</a> </div> </div> </div> <div class="card"> <img src="assets/m3.jpg" alt="Quiet Echo poster"> <div class="pad"> <h4>Quiet Echo</h4> <p>Drama • 2h 04m</p> <div class="row"> <span class="badge">Subtitled</span> <a class="badge" href="movies.php?service=<?php echo rawurlencode('Movie - Quiet Echo'); ?>">View</a> </div> </div> </div> <div class="card"> <img src="assets/m4.jpg" alt="Neon Drift poster"> <div class="pad"> <h4>Neon Drift</h4> <p>Thriller • 1h 45m</p> <div class="row"> <span class="badge">2D</span> <a class="badge" href="movies.php?service=<?php echo rawurlencode('Movie - Neon Drift'); ?>">View</a> </div> </div> </div> </div> </div> </section> <section class="section" style="padding-top:28px;padding-bottom:48px"> <div class="container"> <h3>Popular Events & Sports</h3> <div class="cards"> <div class="card"> <img src="assets/e1.jpg" alt="Acoustic Nights"> <div class="pad"> <h4>Acoustic Nights</h4> <p>Live music • Fri 8 PM</p> <div class="row"> <span class="badge">Limited</span> <a class="badge" href="events.php?service=<?php echo rawurlencode('Event - Acoustic Nights'); ?>">View</a> </div> </div> </div> <div class="card"> <img src="assets/s1.jpg" alt="City FC vs United"> <div class="pad"> <h4>City FC vs United</h4> <p>Football • Sat 6 PM</p> <div class="row"> <span class="badge">Hot</span> <a class="badge" href="sports.php?service=<?php echo rawurlencode('Sport - City FC vs United'); ?>">View</a> </div> </div> </div> <div class="card"> <img src="assets/e3.jpg" alt="Tech Expo"> <div class="pad"> <h4>Tech Expo</h4> <p>Expo • Sun 10 AM</p> <div class="row"> <span class="badge">Expo</span> <a class="badge" href="events.php?service=<?php echo rawurlencode('Event - Tech Expo'); ?>">View</a> </div> </div> </div> </div> </div> </section> <?php include __DIR__ . '/shared-footer.php'; ?> <script> // Tabs logic: Movies / Events / Sports 
    const tabs = Array.from(document.querySelectorAll('.tab')); const categoryInput = document.getElementById('category'); const searchForm = document.getElementById('searchForm'); const qInput = document.getElementById('q'); function activate(tabEl){ tabs.forEach(t=> t.classList.toggle('active', t===tabEl)); const tabName = tabEl.dataset.tab || 'movies'; categoryInput.value = tabName; // Change placeholder and where form submits 
    if(tabName === 'movies'){ qInput.placeholder = 'Search for Movies'; searchForm.action = 'movies.php'; } else if(tabName === 'events'){ qInput.placeholder = 'Search for Events'; searchForm.action = 'events.php'; } else if(tabName === 'sports'){ qInput.placeholder = 'Search for Sports'; searchForm.action = 'sports.php'; } } // init: default movies 
    activate(document.querySelector('.tab[data-tab="movies"]')); // clicks 
    tabs.forEach(t=>{ t.addEventListener('click', ()=>{ activate(t); // small scroll on mobile to show form results 
                                                       if(window.innerWidth < 700) window.scrollTo({ top: document.getElementById('top').offsetTop, behavior:'smooth'}); }); }); // preserve chosen category in url or localStorage if needed // restore from query param if present 
    (function initFromUrl(){ const params = new URLSearchParams(location.search); const cat = params.get('category') || params.get('c') || ''; if(cat){ const target = document.querySelector('.tab[data-tab="'+cat+'"]'); if(target) activate(target); } })(); </script> </body> </html>