:root{ --bg:#0b0f28; --bg-2:#0a0e26; --card:rgba(255,255,255,0.06); --muted:rgba(255,255,255,0.75); --line:rgba(255,255,255,0.08); --primary:#6c5ce7; --primary-2:#a56cff; --text:#ffffff; --radius:16px; --radius-sm:10px; --shadow:0 8px 24px rgba(0,0,0,.45); } *{box-sizing:border-box} html,body{height:100%} body{margin:0;background:linear-gradient(180deg,#0b0f28 0%, #05081a 100%);font-family:Inter,Arial,Helvetica,sans-serif;color:var(--text);-webkit-font-smoothing:antialiased} a{text-decoration:none;color:var(--text)} .container{width:92%;max-width:1200px;margin:0 auto} .nav{position:sticky;top:0;z-index:50;background:rgba(14,18,45,0.6);backdrop-filter:blur(6px);border-bottom:1px solid var(--line)} .nav-inner{display:flex;align-items:center;justify-content:space-between;padding:14px 0} .logo{display:flex;align-items:center;gap:10px;font-weight:800;font-size:20px;letter-spacing:0.5px} .nav-links a{margin:0 12px;font-weight:600;opacity:.95} .nav-links a.active{color:var(--primary)} .cta{padding:8px 14px;border:1px solid var(--line);border-radius:10px;font-weight:700} .hero{ position:relative; padding:100px 0 40px; background: linear-gradient(180deg, rgba(11,15,40,0.5) 0%, rgba(11,15,40,0.95) 100%), url('assets/bg.jpg'); background-size:cover; background-position:center; } .hero .container{text-align:center} .hero h1{ font-size:42px; line-height:1.02; margin:0 0 10px; font-weight:800; letter-spacing:0.4px; } .hero p{font-size:16px;color:var(--muted);margin:0 auto 22px;max-width:760px} /* search card */ .search-card{max-width:980px;margin:18px auto 0;padding:18px;border-radius:14px;background:linear-gradient(90deg, rgba(255,255,255,0.03), rgba(255,255,255,0.02));border:1px solid var(--line);box-shadow:var(--shadow);display:flex;flex-direction:column;gap:12px} .search-tabs{display:flex;gap:10px;justify-content:center} .tab{padding:10px 18px;border-radius:999px;background:transparent;border:1px solid var(--line);font-weight:700;cursor:pointer;user-select:none} .tab.active{background:var(--primary);border-color:transparent;color:#fff;box-shadow:0 6px 20px rgba(108,92,231,0.16)} .search-form{display:grid;grid-template-columns:2fr 1fr 1fr auto;gap:10px;align-items:center} .input,select{background:#0f1430;color:#fff;border:1px solid var(--line);padding:12px 12px;border-radius:10px;width:100%;outline:none;font-size:14px} .search-btn{background:var(--primary);padding:12px 18px;border:0;border-radius:10px;color:#fff;font-weight:800;cursor:pointer} .icon{opacity:.9;margin-right:8px} /* features */ .section{padding:52px 0} .section h3{margin:0 0 14px;font-size:20px} .cards{display:grid;grid-template-columns:repeat(auto-fit,minmax(220px,1fr));gap:16px} .card{background:var(--card);border:1px solid var(--line);border-radius:12px;overflow:hidden;box-shadow:var(--shadow)} .card img{width:100%;height:220px;object-fit:cover;display:block} .card .pad{padding:12px} .card h4{margin:0;font-size:16px} .card p{margin:8px 0 0;color:var(--muted);font-size:13px} .card .row{display:flex;justify-content:space-between;align-items:center;margin-top:12px;gap:8px} .badge{display:inline-block;padding:6px 10px;border-radius:999px;background:rgba(255,255,255,0.03);border:1px solid var(--line);font-size:12px;color:var(--muted)} /* responsive */ @media (max-width:920px){ .search-form{grid-template-columns:1fr 1fr 1fr auto} .hero h1{font-size:36px} } @media (max-width:700px){ .nav-inner{padding:12px 0} .nav-links a{display:inline-block} .search-card{padding:14px} .search-form{grid-template-columns:1fr;gap:10px} .search-tabs{justify-content:space-between} .hero{padding:70px 0 30px} .hero h1{font-size:30px} } .footer{padding:24px 0;border-top:1px solid var(--line);background:rgba(14,18,45,0.6);backdrop-filter:blur(6px);text-align:center;color:var(--muted)}
